const getTheme=()=>{const e=localStorage.getItem("pref-theme"),t=document.documentElement.getAttribute("data-default-theme");return e?e:t==="auto"?window.matchMedia("(prefers-color-scheme: dark)").matches?"dark":"light":t},setTheme=e=>{e==="dark"?document.body.classList.add("dark"):document.body.classList.remove("dark"),localStorage.setItem("pref-theme",e)};document.addEventListener("DOMContentLoaded",()=>{const t=getTheme();setTheme(t);const e=document.getElementById("theme-toggle");e&&e.addEventListener("click",()=>{const e=getTheme(),t=e==="dark"?"light":"dark";setTheme(t)})})