(()=>{document.addEventListener("DOMContentLoaded",function(){const n=document.getElementById("menu-trigger"),e=document.querySelector(".hamburger-icon"),t=document.querySelector(".mobile-menu-overlay"),s=document.body;if(n&&e&&t){n.addEventListener("click",function(){e.classList.toggle("open"),t.classList.toggle("active"),s.classList.toggle("no-scroll")});const o=document.querySelectorAll(".mobile-menu a");o.forEach(n=>{n.addEventListener("click",function(){e.classList.remove("open"),t.classList.remove("active"),s.classList.remove("no-scroll")})})}})})()